sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        var totalAmount = g_form.getValue('linked_po.total_amount');
		g_form.addInfoMessage(totalAmount); 
		var grossAmount = g_form.getValue('gross_invoice_amount');
		g_form.addInfoMessage(grossAmount); 
        if (totalAmount === grossAmount) {
            g_form.showFieldMsg('gross_invoice_amount', 'Invoice amount matches PO amount');
        }
		else{
			g_form.showFieldMsg('gross_invoice_amount', 'Invoice amount does not match PO amount!', 'error');
		}
    }

    //Type appropriate comment here, and begin script below

}
